import React, { useState, useEffect } from 'react';

// App Component
function App() {
  const [trackedActions, setTrackedActions] = useState(() => {
    return JSON.parse(localStorage.getItem('trackedActions')) || [];
  });

  useEffect(() => {
    localStorage.setItem('trackedActions', JSON.stringify(trackedActions));
  }, [trackedActions]);

  const addAction = (action) => {
    setTrackedActions((prevActions) => {
      const existingAction = prevActions.find((a) => a.name === action.name);
      if (existingAction) {
        return prevActions.map((a) =>
          a.name === action.name ? { ...a, count: a.count + 1 } : a
        );
      }
      return [...prevActions, { ...action, count: 1 }];
    });
  };

  const clearActions = () => setTrackedActions([]);

  const removeAction = (actionName) => {
    setTrackedActions((prevActions) =>
      prevActions.filter((a) => a.name !== actionName)
    );
  };

  const totalCarbonReduction = trackedActions.reduce(
    (total, action) => total + action.carbonReduction * action.count,
    0
  );

  return (
    <div>
      <ActionList onAddAction={addAction} />
      <ImpactSummary
        trackedActions={trackedActions}
        totalCarbonReduction={totalCarbonReduction}
        onClearActions={clearActions}
        onRemoveAction={removeAction}
      />
    </div>
  );
}

// ActionList Component
function ActionList({ onAddAction }) {
  const actions = [
    { name: 'Recycled Plastic Bottle', carbonReduction: 0.05 },
    { name: 'Used Public Transport', carbonReduction: 0.2 },
    { name: 'Used Reusable Bag', carbonReduction: 0.02 },
  ];

  return (
    <div>
      <h3>Eco-Friendly Actions</h3>
      {actions.map((action) => (
        <div key={action.name}>
          <span>{action.name} - COâ‚‚ Reduction: {action.carbonReduction} kg</span>
          <button onClick={() => onAddAction(action)}>Add</button>
        </div>
      ))}
    </div>
  );
}

// ImpactSummary Component
function ImpactSummary({ trackedActions, totalCarbonReduction, onClearActions, onRemoveAction }) {
  const treesSaved = Math.floor(totalCarbonReduction / 10);

  const getImpactMessageStyle = () => {
    if (totalCarbonReduction < 0.5) return { color: 'red' };
    if (totalCarbonReduction < 1) return { color: 'orange' };
    return { color: 'green' };
  };

  return (
    <div>
      <h3>Impact Summary</h3>
      {trackedActions.length === 0 ? (
        <p>No actions tracked yet. Start adding eco-friendly actions!</p>
      ) : (
        <>
          <p>Total COâ‚‚ Reduction: {totalCarbonReduction.toFixed(2)} kg</p>
          <p style={getImpactMessageStyle()}>
            You've saved the equivalent of {treesSaved} trees planted!
          </p>
          <button onClick={onClearActions}>Clear All</button>
          {trackedActions.map((action) => (
            <Action
              key={action.name}
              name={action.name}
              count={action.count}
              carbonReduction={action.carbonReduction * action.count}
              onRemove={() => onRemoveAction(action.name)}
            />
          ))}
        </>
      )}
    </div>
  );
}

// Action Component
function Action({ name, count, carbonReduction, onRemove }) {
  return (
    <div>
      <span>{name} - Count: {count}, Total COâ‚‚ Reduction: {carbonReduction.toFixed(2)} kg</span>
      <button onClick={onRemove}>Delete</button>
    </div>
  );
}

export default App;
