import React from 'react';
import './ActionList.css';

function ActionList({ onAddAction }) {
  const actions = [
    { name: 'Use a reusable water bottle', carbonReduction: 0.5 },
    { name: 'Take public transport', carbonReduction: 2.8 },
    { name: 'Eat a plant-based meal', carbonReduction: 0.8 },
    { name: 'Ise energy-efficient llight bulbs', carbonReduction: 0.1 },
    { name: 'Recycle paper', carbonReduction: 0.2 }
  ];

  return (
    <div className="action-list">
      <h3>Eco-Friendly Actions</h3>
      {actions.map((action) => (
        <div key={action.name} className="action-item">
          <span>{action.name} - COâ‚‚ Reduction: {action.carbonReduction} kg</span>
          <button onClick={() => onAddAction(action)}>Add</button>
        </div>
      ))}
    </div>
  );
}

export default ActionList;
